import { USER_MAIN_DATA } from '../data/mocked-data'
import { USER_ACTIVITY } from '../data/mocked-data'
import { USER_AVERAGE_SESSIONS } from '../data/mocked-data'
import { USER_PERFORMANCE } from '../data/mocked-data'

export class Modelisation {
  constructor(data) {
    this.data = data
  }

  formatDataActivity() {
    return {
      sessions: this.data.map((el) => el.sessions),
    }
  }

  formatDataScore() {
    return {
      todayScore: this.data.map((el) => el),
    }
  }
}

console.log(USER_ACTIVITY)
const dataApi = USER_ACTIVITY
console.log(dataApi)

const maClasse = new Modelisation(dataApi)
console.log(maClasse)

//formatDataActivity()
const maClasseformatDataActivity = maClasse.formatDataActivity()
console.log(maClasseformatDataActivity)

//formatDataScore()
const maClasseformatDataScore = maClasse.formatDataScore()
console.log(maClasseformatDataScore)
