import React from 'react'
import { useEffect, useState } from 'react'
import { USER_MAIN_DATA } from '../../data/mocked-data'
import { Modelisation } from '../../data/Modelisation'
import './style.scss'
import { useParams } from 'react-router-dom'

function Survey() {
  const { questionNumber } = useParams()

  return (
    <div>
      <h1>Questionnaire ðŸ§®</h1>
      <h2>Question {questionNumber}</h2>
    </div>
  )
}

const Bonjour = (props) => {
  const [data, setData] = useState(null)
  const { utilisateur } = useParams()

  useEffect(() => {
    const modelisation = new Modelisation(USER_MAIN_DATA)
    console.log(modelisation)
    setData(modelisation.formatUserName())
  }, [])

  console.log('console.log daata', data)
  const name = props.name || ' !'
  const congrats = props.congrats

  if (data !== null) {
    console.log(data)
    return (
      <div className="bonjour">
        <h1>
          Bonjour <span>{name}</span>
          {/* useParams? */}
          Bonjour <span>{data.name[0]}</span>
        </h1>
        <p>{congrats}</p>
      </div>
    )
  }
}

export default Bonjour
