import { USER_MAIN_DATA } from '../data/mocked-data'
import { USER_ACTIVITY } from '../data/mocked-data'
import { USER_AVERAGE_SESSIONS } from '../data/mocked-data'
import { USER_PERFORMANCE } from '../data/mocked-data'

export class Modelisation {
  constructor(data) {
    this.data = data
  }

  //voir pour filtrer par id
  formatUserName(params) {
    // function filtrerParID(obj) {
    //   // Si c'est un nombre
    //   console.log(obj)
    //   if (obj.id === 18) {
    //     return true
    //   } else {
    //     return false
    //   }
    // }

    // var arrByID = arr.filter(filtrerParID);

    // for (let element of this.data) {
    //   console.log(this.data)
    //   console.log(`ELEMENT ${element}`)
    //   const result = this.data.map((el) => el.userInfos.firstName)
    //   const result2 = result.filter(filtrerParID)

    for (let element of this.data) {
      console.log('params.id dans modelisation', params.id)
      if (element.id === params.id) {
        console.log('element dans le for of', element.id)
        return {
          // utiliser for ... in
          name: element,
          // name: `${result2}`,
        }
      }
    }
    // return {
    //   // utiliser for ... in
    //   name: this.data.map((el) => el.userInfos.firstName),
    //   // name: `${result2}`,
    // }
    // }
  }

  formatDataScore() {
    return {
      todayScore: this.data.map((el) => el.todayScore),
    }
  }

  formatDataSessions() {
    return {
      sessions: this.data.map((el) => el.sessions),
    }
  }

  formatDataActivity() {
    return {
      //filter ?
      name: 'Page A',
      uv: 4000,
      pv: 2400,
      amt: 2400,

      day: this.data.map((el) => el.sessions.day),
      kilograms: this.data.map((el) => el.sessions.kilogram),
      calories: this.data.map((el) => el.sessions.calories),
    }
  }

  formatDataRadarChart() {
    return {
      subject: 'Math',
      A: 120,
      B: 110,
      fullMark: 150,
    }
  }

  formatDataPerformances() {
    return {
      kind: this.data.map((el) => el.kind),
      data: this.data.map((el) => el.data),
    }
  }
}

